# --- PowerShell Script to Download and Set Desktop Wallpaper ---

# This script downloads an image from a specified URL, saves it locally,
# and sets it as the desktop wallpaper, stretched to fit the screen.

# Define the URL of the wallpaper image
$url = 'https://raw.githubusercontent.com/SysAdminDoc/Tools/refs/heads/main/Maven_Wallpaper.jpg'

# Define the path to save the wallpaper on the local machine.
# Using the temp directory is a good practice for temporary files.
$wallpaperPath = "$env:TEMP\wallpaper.jpg"

Write-Host "Downloading wallpaper from $url..."
try {
    # Download the image file from the URL and save it to the specified path.
    # -UseBasicParsing is often used to avoid issues with Internet Explorer dependencies.
    Invoke-WebRequest -Uri $url -OutFile $wallpaperPath -UseBasicParsing -TimeoutSec 30
    Write-Host "Wallpaper successfully downloaded to $wallpaperPath"
}
catch {
    Write-Error "Failed to download the wallpaper. Please check the URL and your internet connection."
    Write-Error $_.Exception.Message
    # Stop the script if the download fails
    exit
}

# --- Set Wallpaper Style to 'Stretch' ---
# To stretch the wallpaper, we need to modify the registry.
# WallpaperStyle '2' corresponds to the 'Stretch' option.
# TileWallpaper '0' ensures that the image is not tiled.
$regKey = "HKCU:\Control Panel\Desktop"
Set-ItemProperty -Path $regKey -Name WallpaperStyle -Value 2
Set-ItemProperty -Path $regKey -Name TileWallpaper -Value 0

# --- Apply the Wallpaper ---
# We use the SystemParametersInfo function from user32.dll to apply the change immediately.
# This is more reliable than just changing the registry key alone.

# Define the C# signature for the SystemParametersInfo function.
$code = @'
using System;
using System.Runtime.InteropServices;
using Microsoft.Win32;

public class Wallpaper {
    [DllImport("user32.dll", CharSet = CharSet.Auto)]
    public static extern int SystemParametersInfo(int uAction, int uParam, string lpvParam, int fuWinIni);

    public static void Set(string path) {
        // Constants for setting the wallpaper
        const int SET_DESKTOP_WALLPAPER = 20;
        const int UPDATE_INI_FILE = 0x1;
        const int SEND_WINDOWS_INI_CHANGE = 0x2;

        // Set the wallpaper using the SystemParametersInfo function
        SystemParametersInfo(SET_DESKTOP_WALLPAPER, 0, path, UPDATE_INI_FILE | SEND_WINDOWS_INI_CHANGE);
    }
}
'@

try {
    # Add the C# code as a new type in the current PowerShell session
    Add-Type -TypeDefinition $code

    Write-Host "Setting desktop wallpaper..."
    # Call the static 'Set' method from our new 'Wallpaper' class
    [Wallpaper]::Set($wallpaperPath)

    Write-Host "Wallpaper has been successfully updated!"
}
catch {
    Write-Error "An error occurred while setting the wallpaper."
    Write-Error $_.Exception.Message
}

# --- End of Script ---

Add-Type -TypeDefinition '
	using System.Drawing;
	using System.Runtime.InteropServices;
	
	public static class WallpaperSetter {
		[DllImport("user32.dll")]
		private static extern bool SetSysColors(
			int cElements, 
			int[] lpaElements,
			int[] lpaRgbValues
		);

		[DllImport("user32.dll")]
		private static extern bool SystemParametersInfo(
			uint uiAction,
			uint uiParam,
			string pvParam,
			uint fWinIni
		);

		public static void SetDesktopBackground(Color color) {
			SystemParametersInfo(20, 0, "", 0);
			SetSysColors(1, new int[] { 1 }, new int[] { ColorTranslator.ToWin32(color) });
		}

		public static void SetDesktopImage(string file) {
			SystemParametersInfo(20, 0, file, 0);
		}
	}
' -ReferencedAssemblies 'System.Drawing';

function Set-WallpaperColor {
	param(
		[string]
		$HtmlColor
	);

	$color = [System.Drawing.ColorTranslator]::FromHtml( $HtmlColor );
	[WallpaperSetter]::SetDesktopBackground( $color );
	Set-ItemProperty -Path 'Registry::HKCU\Software\Microsoft\Windows\CurrentVersion\Explorer\Wallpapers' -Name 'BackgroundType' -Type 'DWord' -Value 1 -Force;
	Set-ItemProperty -Path 'Registry::HKCU\Control Panel\Desktop' -Name 'WallPaper' -Type 'String' -Value '' -Force;
	Set-ItemProperty -Path 'Registry::HKCU\Control Panel\Colors' -Name 'Background' -Type 'String' -Value "$($color.R) $($color.G) $($color.B)" -Force;
}

function Set-WallpaperImage {
	param(
		[string]
		$LiteralPath
	);

	if( $LiteralPath | Test-Path ) {
		[WallpaperSetter]::SetDesktopImage( $LiteralPath );
		Set-ItemProperty -Path 'Registry::HKCU\Software\Microsoft\Windows\CurrentVersion\Explorer\Wallpapers' -Name 'BackgroundType' -Type 'DWord' -Value 0 -Force;
		Set-ItemProperty -Path 'Registry::HKCU\Control Panel\Desktop' -Name 'WallPaper' -Type 'String' -Value $LiteralPath -Force;
	} else {
		"Cannot use '$LiteralPath' as a desktop wallpaper because that file does not exist.";
	}
}
Set-WallpaperImage -LiteralPath 'C:\Windows\Setup\Scripts\Wallpaper';

# --- Section 1: Enable Ultimate Performance Power Scheme ---
$guid = 'e9a42b02-d5df-448d-aa00-03f14749eb61'
$out = & "$env:windir\System32\powercfg.exe" /DuplicateScheme $guid

if ($out -match '\s([a-f0-9-]{36})\s') {
    $newSchemeGuid = $Matches[1]
    & "$env:windir\System32\powercfg.exe" /SetActive $newSchemeGuid
}

# --- Section 2: Install Applications via Winget ---
$applications = @(
    @{ Name = "7-Zip"; Id = "7zip.7zip" },
    @{ Name = "TeamViewer"; Id = "TeamViewer.TeamViewer" },
    @{ Name = "Dell Command Update"; Id = "Dell.CommandUpdate" }
)

foreach ($app in $applications) {
    winget install --exact --id $app.Id --silent --accept-package-agreements --accept-source-agreements --scope machine
}

# --- Section 3: Configure Power and System Settings ---
powercfg.exe /HIBERNATE OFF
powercfg -change -monitor-timeout-ac 30
powercfg -change -disk-timeout-ac 0
powercfg -change -standby-timeout-ac 0
powercfg -change -hibernate-timeout-ac 0
powercfg -change -hibernate-timeout-dc 0

# --- Section 4: Configure Boot Manager ---
bcdedit /set {bootmgr} displaybootmenu yes
bcdedit /timeout 3

# --- Section 5: Disable IPv6 on Network Adapters ---
try {
    $adapters = Get-NetAdapterBinding -ComponentID ms_tcpip6 | Where-Object { $_.Enabled }
    
    if ($adapters) {
        Disable-NetAdapterBinding -Name $adapters.Name -ComponentID ms_tcpip6 -PassThru | Out-Null
    }
}
catch {
    # Errors are silently ignored
}

